import { onConnectionError } from './onConnectionError';

/**
 * ```js
 * import { onClientBanned } from '@amityco/ts-sdk'
 * const dispose = onClientBanned(() => {
 *   // ...
 * })
 * ```
 *
 * Fired when any {@link Amity.Client} have been banned from the platform
 *
 * @param callback The function to call when the event was fired
 * @returns an {@link Amity.Unsubscriber} function to stop listening
 *
 * @category Client Events
 */
export const onClientBanned = (callback: Amity.Listener<void>): Amity.Unsubscriber => {
  return onConnectionError(({ code }) => {
    code === Amity.ServerError.GLOBAL_BAN && callback();
  });
};
